import React, { Component } from "react";

import AppHeader from '../app-header';
import TodoList from '../todo-list';
import AddingPanel from '../adding-panel';
import Nav from '../nav';

export default class App extends Component {
  state = {
    appData: [
      {label: 'have a lunch', important: true, id: 'lnch'},
      {label: 'have a dinner', important: true, id: 'dnr'},
      {label: 'have a brunch', important: false, id: 'brnch'},
    ]
  };

  activeTasks = 2;
  doneTasks = 4; 

  deleteItem = (id) => {
    this.setState(({appData}) => {

      const idx = appData.findIndex((el) => id === el.id);
      const updatedData = [...appData.slice(0, idx), ...appData.slice(idx + 1)];
      return{
        appData: updatedData,
      }

    }); 

  }  


  render() {
    return (
    <div className = 'app' style={mainStyles}>
      <AppHeader  active = {activeTasks} done = {doneTasks} />
      <Nav />
      <TodoList 
      todos = {this.appData}
      onDeleted = {this.deleteItem}
      />

      <AddingPanel />
     </div>
   );
  }
  };
