import React from 'react';
import ReactDOM from 'react-dom';

importScripts 

const SearchPanel = () => {
  return <input placeholder='Type here...' />
};

const Filters = () => {
  return (
    <div class='filterButtons'>
      <button class='all'>all</button>
      <button class='active'>active</button>
      <button class='done'>done</button>
    </div>
  );
};

const Nav = () => {
  return (
    <div class = 'navigation'>
      <SearchPanel />
      <Filters />
    </div>
  );
};

const TodoList = () => {
  return (
    <ul>
      <li>Task 1</li>
      <li>Task 2</li>
    </ul>
  );
};

const AddingSpace = () => {
  return <input class = 'addSpace' placeholder='Type here...' />
};

const AddButton = () => {
  return <button>Add</button>
};

const AddingPanel = () => {
  return (
    <div>
      <AddingSpace />
      <AddButton />
    </div>
  );
};

const App = () => {
  return (
    <div class = 'app'>
      <AppHeader />
      <Nav />
      <TodoList />
      <AddingPanel />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));
