    </div>
  );
};

const App = () => {
  return (
    <div class = 'app'>
      <AppHeader />
      <Nav />
      <TodoList />
      <AddingPanel />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));
