import React from 'react';
import ReactDOM from 'react-dom';

import AppHeader from './components/app-header';
import TodoList from './components/todo-list';
import AddingSpace from './components/adding-space';
import AddButton from './components/add-button';




const AddingPanel = () => {
  return (
    <div>
      <AddingSpace />
      <AddButton />
    </div>
  );
};

const App = () => {
  const appData = [
    {label: 'have a lunch', important: true, id: 'lnch'},
    {label: 'have a dinner', important: true, id: 'dnr'},
    {label: 'have a brunch', important: false, id: 'brnch'},
  ];

  const activeTasks = 2;
  const doneTasks = 4; 

  return (
    <div className = 'app'>
      <AppHeader  active = {activeTasks} done = {doneTasks} />
      <Nav />
      <TodoList todos = {appData}/>
      <AddingPanel />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));
