import React, { Component } from "react";

import AppHeader from '../app-header';
import TodoList from '../todo-list';
import AddingPanel from '../adding-panel';
import Nav from '../nav';

export default class App extends Component {

maxId = 100;

  state = {
    appData: [
      {label: 'have a lunch', important: true, id: 1},
      {label: 'have a dinner', important: true, id: 2},
      {label: 'have a brunch', important: false, id: 3},
    ]
  };



  deleteItem = (id) => {
    this.setState(({appData}) => {

      const idx = appData.findIndex((el) => el.id === id);
      const updatedData = [...appData.slice(0, idx), ...appData.slice(idx + 1)];

      return{
        appData: updatedData
      };

    });
  };

  addItem = (text) => {
    const newItem = {
      label: text,
      important: false,
      id: this.maxId++,
    }

    this.setState()
  };

  render() {
    const activeTasks = 2;
    const doneTasks = 4; 
    return (
    <div className = 'app'>
      <AppHeader  active = {activeTasks} done = {doneTasks} />
      <Nav />
      <TodoList 
      todos = {this.state.appData}
      onDeleted = {this.deleteItem}
      />

      <AddingPanel 
      onAdd = {this.addItem}/>
     </div>
   );
  }
};
